function validatePassword(t){return null!=new RegExp("^(?=.*[a-zA-Z])(?=.*[!@#$%^*+=-])(?=.*[0-9]).{8,}$","g").exec(t)}window.onload=function(){$("#submit-btn").click(function(){const t=document.getElementById("requestURL").getAttribute("value").trim(),e=document.getElementById("token").getAttribute("value").trim(),n=document.getElementById("password").value.trim();n===document.getElementById("password_confirm").value.trim()?!1!==validatePassword(n)?$.ajax({url:t,type:"post",contentType:"application/json; charset=utf-8",data:JSON.stringify({token:e,password:sha256(n)}),success:function(){alert("비밀번호 변경에 성공했습니다.\n변경된 비밀번호로 로그인해주세요."),window.close()},error:function(){alert("알 수 없는 오류가 발생했습니다. 해당 증상이 지속된다면 관리자에게 문의하세요.")}}):alert("비밀번호는 최소 8글자 이상이고 특수문자를 포함한 영문자 및 숫자만으로 이루어져야 합니다."):alert("비밀번호가 일치하지 않습니다. 다시 입력해주세요.")})};